INSERT INTO `wp_options` VALUES (1086,'_transient_dash_88ae138922fe95674369b1cb3d215a2b','<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2016/12/vaughan/\'>WordPress 4.7 “Vaughan”</a> <span class=\"rss-date\">December 6, 2016</span><div class=\"rssSummary\">Version 4.7 of WordPress, named “Vaughan” in honor of legendary jazz vocalist Sarah &quot;Sassy&quot; Vaughan, is available for download or update in your WordPress dashboard. New features in 4.7 help you get your site set up the way you want it.</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://heropress.com/help-heropress-friend/\'>HeroPress: Help a HeroPress friend</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/wpweekly-episode-258-2016-year-in-review-part-1\'>WPTavern: WPWeekly Episode 258 – 2016 Year in Review Part 1</a></li><li><a class=\'rsswidget\' href=\'https://poststatus.com/post-status-draft-live-at-publish/\'>Post Status: What we want from WordPress in 2017, live from Post Status Publish</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\"dashboard-news-plugin\"><span>Popular Plugin:</span> Google Analytics Dashboard for WP&nbsp;<a href=\"plugin-install.php?tab=plugin-information&amp;plugin=google-analytics-dashboard-for-wp&amp;_wpnonce=1c5de41ebf&amp;TB_iframe=true&amp;width=600&amp;height=800\" class=\"thickbox open-plugin-details-modal\" aria-label=\"Install Google Analytics Dashboard for WP\">(Install)</a></li></ul></div>','no');
